mutation addCustomerInfo($customerInput: _CreateCustomerInput!) {
  packet {
    updateOrCreateCustomer(input: $customerInput) {
      returning {
        ...CustomerAttributes
      }
    }
  }
}
    fragment CustomerAttributes on _E_Customer {
  id
  __typename
  data {
    firstName
    lastName
  }
  defaultDeliveryAddress {
    ...AddressAttributes
  }
}
    fragment AddressAttributes on _G_DeliveryAddress {
  countryIso
  regionIso
  city
  street
  flatNumber
}

mutation addGoodTypeInfo($goodTypeInput: _CreateGoodTypeInput!) {
  packet {
    updateOrCreateGoodType(input: $goodTypeInput) {
      returning {
        ...GoodTypeAttributes
      }
    }
  }
}
    fragment GoodTypeAttributes on _E_GoodType {
  id
  __typename
  name
  descr
  price
  vendor {
    entity {
      id
      __typename
      name
    }
  }
}

mutation addOrderDetail($customerId: String!, $goodTypeId: String!, $deliveryAddress: _DeliveryAddressInput!) {
  packet {
    updateOrCreateOrder(
      input: {openOrderFlag: $customerId, customer: {entityId: $customerId}, deliveryAddress: $deliveryAddress}
      exist: {byKey: openOrderFlag}
    ) {
      returning {
        ...OrderAttributes
      }
    }
    createOrderDetail(
      input: {order: "ref:updateOrCreateOrder", goodType: {entityId: $goodTypeId}}
    ) {
      ...OrderDetailAttributes
    }
  }
}
    fragment OrderAttributes on _E_Order {
  id
  __typename
  openOrderFlag
  orderDate
  comment
  statusForVendor {
    code
  }
  orderDetailList {
    elems {
      ...OrderDetailAttributes
    }
  }
}
    fragment OrderDetailAttributes on _E_OrderDetail {
  id
  __typename
  goodType {
    entity {
      ...GoodTypeAttributes
    }
  }
}
    fragment GoodTypeAttributes on _E_GoodType {
  id
  __typename
  name
  descr
  price
  vendor {
    entity {
      id
      __typename
      name
    }
  }
}
fragment OrderDetailAttributes on _E_OrderDetail {
  id
  __typename
  goodType {
    entity {
      ...GoodTypeAttributes
    }
  }
}
    fragment GoodTypeAttributes on _E_GoodType {
  id
  __typename
  name
  descr
  price
  vendor {
    entity {
      id
      __typename
      name
    }
  }
}

mutation createPckg($vendorId: ID!, $serialNumber: String!, $deliveryAddress: _DeliveryAddressInput!, $orderDetailId: ID!, $orderDetailRef: String!, $orderRef: String!) {
  p1: packet {
    createPckg(
      input: {vendor: $vendorId, serialNumber: $serialNumber, deliveryAddress: $deliveryAddress, orderDetail: {rootEntityId: $orderRef, entityId: $orderDetailRef}}
    ) {
      id
    }
  }
  p2: packet {
    updateOrderDetail(
      input: {id: $orderDetailId, statusForVendor: {code: "PACKAGED"}}
    ) {
      id
    }
  }
}

mutation fixOrder($orderId: ID!) {
  packet {
    updateOrder(
      input: {id: $orderId, openOrderFlag: null, statusForVendor: {code: "FIXED"}}
    ) {
      ...OrderAttributes
    }
  }
}
    fragment OrderAttributes on _E_Order {
  id
  __typename
  openOrderFlag
  orderDate
  comment
  statusForVendor {
    code
  }
  orderDetailList {
    elems {
      ...OrderDetailAttributes
    }
  }
}
    fragment OrderDetailAttributes on _E_OrderDetail {
  id
  __typename
  goodType {
    entity {
      ...GoodTypeAttributes
    }
  }
}
    fragment GoodTypeAttributes on _E_GoodType {
  id
  __typename
  name
  descr
  price
  vendor {
    entity {
      id
      __typename
      name
    }
  }
}

