query searchOrderDetailForVendor($cond: String) {
  searchOrderDetail(cond: $cond) {
    elems {
      ...OrderDetailForVendorAttributes
    }
  }
}
    fragment OrderDetailForVendorAttributes on _E_OrderDetail {
  id
  __typename
  statusForVendor {
    code
  }
  order {
    id
    deliveryAddress {
      ...AddressAttributes
    }
  }
  goodType {
    entity {
      id
      __typename
      name
      vendor {
        entityId
      }
    }
  }
}
    fragment AddressAttributes on _G_DeliveryAddress {
  countryIso
  regionIso
  city
  street
  flatNumber
}
